class Solution:
    def calcCounts(self, s):
        ret = dict()
        for w in s:
            if w in ret:
                ret[w] += 1
            else:
                ret[w] = 1
        return ret

    def success(self, counts, k):
        for _, v in counts.items():
            if v < k and v != 0:
                return False
        return True
    def longestSubstring(self, s, k):
        """
        :type s: str
        :type k: int
        :rtype: int
        """
        counts = self.calcCounts(s)
        forward = True
        p = 0
        slen = len(s)
        for i in range(slen, k-1, -1):
            for j in range(slen-i+1):
                if self.success(counts, k):
                    return i
                if j == len(s)-i:
                    break
                ne = 1 if forward else -1
                counts[s[p]] -= 1
                nc = s[p+ne*i]
                counts[nc] += 1
                p += ne

            counts[s[p]] -= 1
            p = len(s)-1 if forward else 0
            forward = not forward
        
        return 0

s = Solution()
ret = s.longestSubstring("", 301)
print(ret)